import { useEffect, useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import Swal from 'sweetalert2';
import jsPDF from 'jspdf';
import QRCode from 'qrcode';

const supabase = createClient(
    import.meta.env.VITE_REACT_APP_SUPABASE_URL!,
    import.meta.env.VITE_REACT_APP_SUPABASE_ANON_KEY!
);

type Booking = {
    concertImage: any;
    created_at: string | number | Date;
    id: string;
    userid: string;
    concertid: string;
    tickets: any[];
    total: number;
    status: boolean;
    userName?: string;
    concertName?: string;
};

const PER_PAGE = 10;

const BookingTable = () => {
    const [bookings, setBookings] = useState<Booking[]>([]);
    const [loading, setLoading] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalBookings, setTotalBookings] = useState(0);
    const [search, setSearch] = useState('');
    const [downloadingId, setDownloadingId] = useState<string | null>(null);

    useEffect(() => {
        fetchBookings();
        fetchTotalCount();
    }, [currentPage]);

    const hashString = async (input: string): Promise<string> => {
        const encoder = new TextEncoder();
        const data = encoder.encode(input);
        const hashBuffer = await crypto.subtle.digest("SHA-256", data);
        return Array.from(new Uint8Array(hashBuffer))
            .map((b) => b.toString(16).padStart(2, "0"))
            .join("");
    };

    const downloadTicket = async (booking: Booking) => {

        setDownloadingId(booking.id);
        try {
            const { data: userProfile, error: userError } = await supabase
                .from('user_profiles')
                .select('first_name, last_name, email')
                .eq('id', booking.userid)
                .single();

            if (userError || !userProfile) {
                console.error('Failed to fetch user profile for booking:', userError);
                Swal.fire('Error', 'Unable to load user profile.', 'error');
                return;
            }

            const { data: concert } = await supabase
                .from('concerts')
                .select('concert_name, concert_location_name, concert_date, concert_start_time, concert_end_time')
                .eq('concert_name', booking.concertName)
                .single()

            const doc = new jsPDF()
            let y = 20
            const pageWidth = doc.internal.pageSize.getWidth()
            const margin = 15

            // Header Text
            doc.setFontSize(16);
            doc.setFont('helvetica', 'bold');
            doc.text('BOOKING VOUCHER', margin, y);

            // Logo on right
            const logoImg = 'data:image/png;base64,'; // replace with your logo base64 string
            const logoWidth = 30;
            const logoHeight = 8;
            const logoX = pageWidth - margin - logoWidth;
            const logoY = y - 6; // align vertically with text

            try {
                doc.addImage(logoImg, 'PNG', logoX, logoY, logoWidth, logoHeight);
            } catch (err) {
                console.warn('Error loading logo:', err);
            }

            try {
                // FIRST IMAGE (e.g. your asset image)
                const assetImg = 'data:image/png;base64,'; // replace with your base64
                const assetWidth = 30;
                const assetHeight = 6;
                const spacing = 5; // space between images
                const assetX = logoX - spacing - assetWidth;
                const assetY = logoY;
                doc.addImage(assetImg, 'PNG', assetX, assetY, assetWidth, assetHeight);

                // THEN YOUR ORIGINAL LOGO
                doc.addImage(logoImg, 'PNG', logoX, logoY, logoWidth, logoHeight);
            } catch (err) {
                console.warn('Error loading images:', err);
            }

            y += 10;

            // Add concert image
            // Add concert image on the left, QR on the right (side by side)
            if (booking.concertImage) {
                const concertImageBase64 = booking.concertImage.startsWith('data:')
                    ? booking.concertImage
                    : `data:image/jpeg;base64,${booking.concertImage}`;
                try {
                    const imgWidth = 40;
                    const imgHeight = 50;
                    const imgX = margin; // Left margin
                    const imgY = y;

                    const qrSize = 48;
                    const qrX = pageWidth - margin - qrSize; // Right aligned
                    const qrY = y; // Same Y as image

                    // Add concert image
                    doc.addImage(concertImageBase64, 'JPEG', imgX, imgY, imgWidth, imgHeight);

                    // Add QR code
                    const rawQrValue = `TICKET-${booking.id}`;
                    const hashedQrValue = await hashString(rawQrValue);
                    const qrDataUrl = await QRCode.toDataURL(hashedQrValue);
                    doc.addImage(qrDataUrl, 'PNG', qrX, qrY, qrSize, qrSize);

                    y += imgHeight + 8; // Move down after both
                } catch (imgErr) {
                    console.warn('Could not load concert image into PDF:', imgErr);
                }
            }


            y += 6;

            doc.setFont('helvetica', 'normal')
            doc.setFontSize(12)
            doc.text(`ORDER REFERENCE NO:  TICKET-${booking.id}`, margin, y)

            y += 6

            console.log('Booking created at:', booking.created_at)
            const orderDate = booking.created_at
                ? new Date(booking.created_at).toLocaleString('en-MU', {
                    year: 'numeric',
                    month: 'short',
                    day: '2-digit',
                    hour: '2-digit',
                    minute: '2-digit',
                })
                : 'N/A'

            doc.setFontSize(10)
            doc.text(`Order Date: ${orderDate}`, margin, y)

            y += 10
            doc.setDrawColor(200)
            doc.setLineWidth(0.5)
            doc.line(margin, y, pageWidth - margin, y)

            // Event title
            y += 10
            doc.setFontSize(14)
            doc.setFont('helvetica', 'bold')
            doc.setTextColor(0, 102, 204)
            doc.text(concert?.concert_name?.toUpperCase() || 'EVENT NAME', pageWidth / 2, y, { align: 'center' })

            // Customer Info
            y += 10
            doc.setTextColor(0)
            doc.setFontSize(12)
            doc.setFont('helvetica', 'bold')
            doc.text('Customer Details', margin, y)

            y += 8
            doc.setFont('helvetica', 'normal')
            doc.text(`Full Name: ${userProfile?.first_name || ''} ${userProfile?.last_name || ''}`, margin, y)

            y += 6
            doc.text(`Email: ${userProfile.email}`, margin, y)

            y += 8
            doc.setFontSize(11)
            doc.setFont('helvetica', 'bold')
            if (booking.status) {
                doc.setTextColor(0, 150, 0) // green
                doc.text('STATUS OF INVOICE: PAID', margin, y)
            } else {
                doc.setTextColor(200, 0, 0) // red
                doc.text('STATUS OF INVOICE: UNPAID', margin, y)
            }
            doc.setTextColor(0) // reset text color


            // Booking Details
            y += 12
            doc.setFont('helvetica', 'bold')
            doc.setFontSize(12)
            doc.text('Booking Details', margin, y)

            y += 8
            doc.setFont('helvetica', 'normal')
            doc.text(`Date: ${concert?.concert_date || '-'}`, margin, y)
            y += 6
            doc.text(`Start Time: ${concert?.concert_start_time || '-'}`, margin, y)
            doc.text(`End Time: ${concert?.concert_end_time || '-'}`, margin + 60, y)

            y += 6
            doc.text(`Location: ${concert?.concert_location_name || '-'}, Mauritius`, margin, y)

            // Tickets
            y += 10
            const { data: ticketData } = await supabase
                .from('tickets')
                .select('id, ticket_name')

            if (Array.isArray(booking.tickets)) {
                doc.setFont('helvetica', 'bold')
                doc.text('Tickets:', margin, y)
                y += 6

                // Table header
                doc.setFontSize(10)
                doc.setFont('helvetica', 'bold')
                doc.text('#', margin + 2, y)
                doc.text('Ticket Name', margin + 15, y)
                doc.text('Qty', margin + 120, y)
                y += 5
                doc.setDrawColor(180)
                doc.line(margin, y, pageWidth - margin, y)
                y += 4

                doc.setFont('helvetica', 'normal')
                booking.tickets.forEach((ticket, index) => {
                    const ticketName = ticketData?.find(t => t.id === ticket.ticket_id)?.ticket_name || `ID ${ticket.ticket_id}`
                    doc.text(String(index + 1), margin + 2, y)
                    doc.text(ticketName, margin + 15, y)
                    doc.text(String(ticket.quantity), margin + 120, y)


                    y += 6
                })
            }

            // Notes
            y += 10;
            doc.setFont('helvetica', 'bold');
            doc.setFontSize(11);
            doc.text('Terms and Conditions – Sam Garrett Live in Mauritius', margin, y);
            y += 8;

            doc.setFont('helvetica', 'normal');
            doc.setFontSize(9);
            const terms = [
                'Terms and Conditions – Sam Garrett Live in Mauritius',

                '1. Ticket Purchase & Entry',
                '* Tickets are non-refundable and non-transferable, unless the event is cancelled or rescheduled.',
                '* Entry will only be granted upon presentation of a valid ticket (physical or digital).',
                '* Lost, stolen, or damaged tickets will not be replaced or refunded.',

                '2. Safety & Conduct',
                'This is a family-friendly, alcohol-free, drug-free, and controlled substance-free event.',
                'Security checks may be conducted at the entry point. Dangerous or prohibited items (including but not limited to weapons, glass containers, and fireworks) are strictly forbidden.',
                'The organisers reserve the right to refuse entry or remove any individual who engages in disruptive, unsafe, or inappropriate behaviour, without refund.',

                '3. Filming & Photography',
                '* By attending, you consent to being filmed and photographed for promotional purposes by the organisers.',
                '* Personal photography is allowed; however, professional equipment (e.g., DSLRs, drones, tripods) is not permitted without prior written approval.',

                '4. Environment & Respect',
                '* Please respect the venue and natural surroundings. Use the bins provided and avoid littering.',
                '* This event promotes conscious, eco-friendly, and sustainable values. Be mindful of your environmental impact.',

                '5. Liability',
                '* The organisers are not responsible for any loss, injury, illness, or damage to persons or personal belongings during the event, including but not limited to theft, accidents, or natural occurrences.',
                '* Attendees participate at their own risk and are solely responsible for their personal safety and belongings.',
                '* By attending, you agree to waive any claims against the organisers, venue, sponsors, artists, and affiliates.',

                '6. Force Majeure',
                '* The organisers shall not be held liable for any cancellation, delay, or alteration of the event due to circumstances beyond their control, including but not limited to natural disasters, extreme weather, strikes, government regulations, or public health emergencies.',
                '* In such cases, the event may be postponed or rescheduled. No refunds will be issued unless otherwise announced by the organisers.',

                '7. Medical Attention',
                '* Basic first aid support will be available on-site, but the organisers are not liable for any medical emergencies.',
                '* Attendees are responsible for managing their own medical conditions',
                '* By attending, you acknowledge and accept full responsibility for your own health and well-being.',

                '8. Assumption of Risk',
                '* Attending a live concert involves inherent risks, including but not limited to crowd density, high sound levels and uneven ground.',
                '* By entering the event premises, you voluntarily assume all such risks.',

                '9. Prohibited Behaviour',
                '* The organisers have a zero-tolerance policy for harassment, aggressive conduct, hate speech, or discriminatory behaviour of any kind.',
                '* Any individual engaging in such behaviour may be removed immediately and without refund, and may be subject to legal action if necessary.',

                '10. Dispute Resolution & Governing Law',
                '* These terms and conditions shall be governed by the laws of the Republic of Mauritius.',
                '* In the event of a dispute or claim arising from this event, all parties agree to first attempt to resolve the matter amicably.',
                '* If an amicable resolution is not possible, the dispute shall be subject to the exclusive jurisdiction of the courts of Mauritius.'
            ];


            terms.forEach(line => {
                const safeBottom = 270;
                const lineHeight = 5;
                const wrapped = doc.splitTextToSize(line, pageWidth - margin * 2);

                wrapped.forEach((wrappedLine: string | string[]) => {
                    if (y + lineHeight > safeBottom) {
                        doc.addPage();
                        y = 20;
                    }
                    doc.text(wrappedLine, margin, y);
                    y += lineHeight;
                });
            });


            // Footer
            y += 5
            doc.setFontSize(10)
            doc.setTextColor(80)
            doc.text('Need Help? Call us at +230 5918 2520 or visit www.zeko.com', margin, y)

            // Save PDF
            doc.save(`Ticket-${booking.id}.pdf`)
        } finally {
            setDownloadingId(null)
        }
    }

    const fetchBookings = async () => {
        setLoading(true);
        const from = (currentPage - 1) * PER_PAGE;
        const to = from + PER_PAGE - 1;

        const { data: bookingsData, error: bookingsError } = await supabase
            .from('bookings')
            .select('*')
            .order('created_at', { ascending: false }) // <-- sort by latest
            .range(from, to);


        if (bookingsError || !bookingsData) {
            console.error(bookingsError);
            setLoading(false);
            return;
        }

        // Unique user and concert IDs
        const userIds = [...new Set(bookingsData.map(b => b.userid))];
        const concertIds = [...new Set(bookingsData.map(b => b.concertid))];

        // Extract all ticket_ids from all bookings
        const ticketIds = Array.from(
            new Set(
                bookingsData
                    .flatMap(b => (Array.isArray(b.tickets) ? b.tickets.map((t: { ticket_id: any; }) => t.ticket_id) : []))
                    .filter(Boolean)
            )
        );

        // Fetch users
        const { data: usersData } = await supabase
            .from('user_profiles')
            .select('id, first_name, last_name')
            .in('id', userIds);

        // Fetch concerts
        const { data: concertsData } = await supabase
            .from('concerts')
            .select('id, concert_name ,concert_image, front_image')
            .in('id', concertIds);

        // Fetch ticket names
        const { data: ticketData } = await supabase
            .from('tickets')
            .select('id, ticket_name')
            .in('id', ticketIds);

        const userMap = new Map(usersData?.map(u => [u.id, `${u.first_name} ${u.last_name}`]));
        const concertMap = new Map(concertsData?.map(c => [c.id.toString(), c.concert_name]));
        const ticketMap = new Map(ticketData?.map(t => [t.id, t.ticket_name]));

        // Merge enriched booking
        const merged = bookingsData.map(b => ({
            ...b,
            userName: userMap.get(b.userid) || b.userid,
            concertName: concertMap.get(b.concertid.toString()) || b.concertid,
            concertImage: concertsData?.find(c => c.id.toString() === b.concertid.toString())?.front_image || null,
            tickets: Array.isArray(b.tickets)
                ? b.tickets.map((t: { ticket_id: any; }) => ({
                    ...t,
                    ticket_name: ticketMap.get(t.ticket_id) || t.ticket_id
                }))
                : []
        }));

        setBookings(merged);
        setLoading(false);
    };

    const fetchTotalCount = async () => {
        const { count } = await supabase
            .from('bookings')
            .select('*', { count: 'exact', head: true });

        if (count !== null) setTotalBookings(count);
    };

    const handleToggleStatus = async (booking: Booking) => {
        const confirm = await Swal.fire({
            title: 'Change Status?',
            text: `This will mark the booking as ${!booking.status ? 'confirmed' : 'pending'}.`,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, change it',
            cancelButtonText: 'Cancel',
        });

        if (!confirm.isConfirmed) return;

        // If confirming the booking (false -> true)
        if (!booking.status) {
            // Fetch latest ticket quantities from DB
            const ticketIds = booking.tickets.map(t => t.ticket_id);
            const { data: currentTickets, error } = await supabase
                .from('tickets')
                .select('id, quantity')
                .in('id', ticketIds);

            if (error) {
                console.error(error);
                Swal.fire('Error', 'Failed to fetch ticket stock.', 'error');
                return;
            }

            const updates = booking.tickets.map((t) => {
                const current = currentTickets?.find(ct => ct.id === t.ticket_id);
                const remainingQty = current ? current.quantity - t.quantity : 0;
                return {
                    id: t.ticket_id,
                    quantity: remainingQty < 0 ? 0 : remainingQty,
                };
            });

            // Batch update tickets
            for (const update of updates) {
                const { error: updateError } = await supabase
                    .from('tickets')
                    .update({ quantity: update.quantity })
                    .eq('id', update.id);

                if (updateError) {
                    console.error(updateError);
                    Swal.fire('Error', `Failed to update ticket ID ${update.id}.`, 'error');
                    return;
                }
            }
        }

        // Update booking status
        const { error: bookingError } = await supabase
            .from('bookings')
            .update({ status: !booking.status })
            .eq('id', booking.id);

        if (!bookingError) {
            fetchBookings();
            Swal.fire('Updated!', 'Booking status updated.', 'success');
        } else {
            Swal.fire('Error', 'Failed to update booking status.', 'error');
        }
    };


    const totalPages = Math.ceil(totalBookings / PER_PAGE);

    const filteredBookings = bookings.filter((b) =>
        b.userName?.toLowerCase().includes(search.toLowerCase())
    );

    return (
        <div>
            <h2 className="text-xl font-bold mb-4">Bookings</h2>

            <input
                type="text"
                className="form-input mb-4 w-full"
                placeholder="Filter by user name..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
            />

            {loading ? (
                <p>Loading bookings...</p>
            ) : (
                <>
                    <table className="table table-striped w-full">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>User</th>
                                <th>Concert</th>
                                <th>Tickets</th>
                                <th>Total</th>
                                <th>Status</th>
                                <th>Ticket</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredBookings.map((b) => (
                                <tr key={b.id}>
                                    <td>{b.id}</td>
                                    <td>{b.userName}</td>
                                    <td>{b.concertName}</td>
                                    <td>
                                        {Array.isArray(b.tickets) && b.tickets.length > 0 ? (
                                            b.tickets.map((t, i) => (
                                                <div key={i}>
                                                    Ticket: {t.ticket_name} , Qty: {t.quantity}
                                                </div>
                                            ))
                                        ) : (
                                            'No tickets'
                                        )}
                                    </td>
                                    <td>MUR {b.total}</td>
                                    <td>
                                        {b.status ? (
                                            <span className="px-2 py-1 text-sm font-semibold text-green-700 bg-green-100 rounded">
                                                Dispatched
                                            </span>
                                        ) : (
                                            <label className="inline-flex items-center cursor-pointer">
                                                <input
                                                    type="checkbox"
                                                    className="sr-only"
                                                    checked={b.status}
                                                    onChange={() => handleToggleStatus(b)}
                                                />
                                                <div className="w-10 h-5 bg-gray-300 rounded-full relative">
                                                    <div className="w-5 h-5 bg-white rounded-full shadow transform transition-transform translate-x-0" />
                                                </div>
                                            </label>
                                        )}
                                    </td>
                                    <td>
                                        <button
                                            className="btn btn-sm btn-outline-success"
                                            onClick={() => downloadTicket(b)}
                                            disabled={downloadingId === b.id}
                                        >
                                            {downloadingId === b.id ? 'Downloading...' : 'Download'}
                                        </button>
                                    </td>

                                </tr>
                            ))}
                        </tbody>
                    </table>

                    <div className="flex justify-between mt-4">
                        <button
                            className="btn btn-primary"
                            disabled={currentPage === 1}
                            onClick={() => setCurrentPage(prev => prev - 1)}
                        >
                            Previous
                        </button>
                        <span className="self-center">Page {currentPage} of {totalPages}</span>
                        <button
                            className="btn btn-primary"
                            disabled={currentPage === totalPages}
                            onClick={() => setCurrentPage(prev => prev + 1)}
                        >
                            Next
                        </button>
                    </div>
                </>
            )}
        </div>
    );
};

export default BookingTable;